                    {{-- Pagination Elements --}}
                    @php
                        $currentPage = $paginator->currentPage();
                        $totalPages = $paginator->lastPage();
                    @endphp

                    @if ($totalPages > 1)
                        {{-- Displaying Previous Ellipsis --}}
                        @if ($currentPage > 3)
                            <span aria-disabled="true">
                                <span
                                    class="relative inline-flex items-center px-4 py-2 -ml-px text-sm font-medium text-gray-700 bg-white border border-gray-300 cursor-default leading-5">...</span>
                            </span>
                        @endif

                        {{-- Displaying Previous 2 Pages --}}
                        @for ($i = max(1, $currentPage - 1); $i <= min($totalPages, $currentPage + 1); $i++)
                            @if ($i == $currentPage)
                                <span aria-current="page">
                                    <span
                                        class="relative inline-flex items-center px-4 py-2 -ml-px text-sm font-medium text-white bg-[#141652] border border-[#141652] cursor-default leading-5">{{ $i }}</span>
                                </span>
                            @else
                                <a href="{{ $paginator->url($i) }}"
                                    class="relative inline-flex items-center px-4 py-2 -ml-px text-sm font-medium text-gray-700 bg-white border border-gray-300 leading-5 hover:text-gray-500 hover:bg-[#f0f0f0] focus:z-10 focus:outline-none focus:ring ring-[#141652]"
                                    aria-label="{{ __('Go to page :page', ['page' => $i]) }}">
                                    {{ $i }}
                                </a>
                            @endif
                        @endfor

                        {{-- Displaying Next Ellipsis --}}
                        @if ($currentPage < $totalPages - 2)
                            <span aria-disabled="true">
                                <span
                                    class="relative inline-flex items-center px-4 py-2 -ml-px text-sm font-medium text-gray-700 bg-white border border-gray-300 cursor-default leading-5">...</span>
